mapper.select:
SELECT f.feedNumber, f.feedContent, f.feedDate, u.userNumber, u.userID, u.userNickName, IF(f.feedNumber IN (SELECT Likes.feedNumber From Likes WHERE Likes.userNumber = #{userNumber}), 1, 0) AS checklike, (SELECT COUNT(userNumber) FROM Likes WHERE Likes.feedNumber = f.feedNumber) AS cntLike, (SELECT COUNT(userNumber) FROM Comment WHERE Comment.feedNumber = f.feedNumber) AS cntComment FROM Feed AS f INNER JOIN User AS u USING(userNumber) where u.userNumber IN (select toUserNumber from Follow where fromUserNumber = #{userNumber}) AND
feedDate < now()
feedDate < #{cursor}
ORDER BY feedDate desc LIMIT 10;

SELECT saveFolder, photoName
FROM FeedPhoto
WHERE feedNumber = #{feedNumber}

SELECT feedNumber, feedThumbnailFolder, feedThumbnailName
FROM Feed
WHERE userNumber = #{userNumber}
