mapper.select:
SELECT userNumber, petNumber, animalNumber, petName, petGender, petBirth, petProfilePicture, petProfileContent
FROM Pet
WHERE userNumber = #{userNumber}

SELECT userNumber, petNumber, animalNumber, petName, petGender, petBirth, petProfilePicture, petProfileContent
FROM Pet
WHERE userNumber = #{userNumber} and petNumber = #{petNumber}

SELECT count(*)
FROM Pet
WHERE userNumber = #{userNumber} AND petName = #{petName}

mapper.update:
UPDATE Pet
SET animalNumber=#{animalNumber}, petName=#{petName}, petGender=#{petGender}, petBirth=#{petBirth}, petProfilePicture=#{petProfilePicture}, petProfileContent=#{petProfileContent}
WHERE userNumber = #{userNumber} and petNumber = #{petNumber}
